{
  "name": "Deal Architect v2",
  "description": "A sophisticated deal-making and financial modeling suite for high-stakes business negotiations and investments. It features real-time financial simulations, data-driven insights, and AI-powered risk assessment."
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export interface DealParams {
  dealName: string;
  initialInvestment: number;
  monthlyRevenue: number;
  monthlyExpenses: number;
  growthRate: number;
  timelineMonths: number;
  exitMultiple: number;
}

export interface ProjectionPoint {
  month: number;
  revenue: number;
  profit: number;
  cumulativeProfit: number;
}

export interface AIAnalysis {
  summary: string;
  strengths: string[];
  weaknesses: string[];
  opportunities: string[];
  threats: string[];
  strategicAdvice: string;
  riskScore: number;
}

import { GoogleGenAI, Type } from "@google/genai";
import { DealParams, AIAnalysis } from "../types";

export const analyzeDeal = async (params: DealParams): Promise<AIAnalysis> => {
  const ai = new GoogleGenAI({ apiKey: process.env.API_KEY || "" });

  const prompt = `Analyze this business deal architecture and provide a comprehensive financial and strategic evaluation.

  Deal Metrics:
  - Name: ${params.dealName}
  - Initial Investment: $${params.initialInvestment}
  - Projected Monthly Revenue: $${params.monthlyRevenue}
  - Projected Monthly Expenses: $${params.monthlyExpenses}
  - Expected Monthly Growth Rate: ${params.growthRate}%
  - Timeline: ${params.timelineMonths} months
  - Exit Multiple (on annual revenue): ${params.exitMultiple}x

  Please perform a SWOT analysis, provide a risk score (0-100), and give strategic advice for negotiation or restructuring.`;

  const response = await ai.models.generateContent({
    model: "gemini-3-pro-preview",
    contents: prompt,
    config: {
      responseMimeType: "application/json",
      responseSchema: {
        type: Type.OBJECT,
        properties: {
          summary: { type: Type.STRING },
          strengths: { type: Type.ARRAY, items: { type: Type.STRING } },
          weaknesses: { type: Type.ARRAY, items: { type: Type.STRING } },
          opportunities: { type: Type.ARRAY, items: { type: Type.STRING } },
          threats: { type: Type.ARRAY, items: { type: Type.STRING } },
          strategicAdvice: { type: Type.STRING },
          riskScore: { type: Type.NUMBER }
        },
        required: ["summary", "strengths", "weaknesses", "opportunities", "threats", "strategicAdvice", "riskScore"]
      }
    }
  });

  return JSON.parse(response.text);
};

import React, { useState, useMemo, useEffect } from 'react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend } from 'recharts';
import { 
  Calculator, 
  TrendingUp, 
  ShieldAlert, 
  Zap, 
  BarChart3, 
  ChevronRight, 
  RefreshCw,
  Wallet,
  ArrowUpRight,
  Target
} from 'lucide-react';
import { DealParams, ProjectionPoint, AIAnalysis } from './types';
import { analyzeDeal } from './services/geminiService';

const App: React.FC = () => {
  const [params, setParams] = useState<DealParams>({
    dealName: "Acme Expansion Project",
    initialInvestment: 500000,
    monthlyRevenue: 45000,
    monthlyExpenses: 28000,
    growthRate: 2.5,
    timelineMonths: 24,
    exitMultiple: 4
  });

  const [aiAnalysis, setAiAnalysis] = useState<AIAnalysis | null>(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  const projections = useMemo(() => {
    const data: ProjectionPoint[] = [];
    let currentRevenue = params.monthlyRevenue;
    let cumulativeProfit = -params.initialInvestment;

    for (let i = 0; i <= params.timelineMonths; i++) {
      const monthlyProfit = currentRevenue - params.monthlyExpenses;
      cumulativeProfit += i === 0 ? 0 : monthlyProfit;

      data.push({
        month: i,
        revenue: Math.round(currentRevenue),
        profit: Math.round(monthlyProfit),
        cumulativeProfit: Math.round(cumulativeProfit)
      });

      currentRevenue *= (1 + params.growthRate / 100);
    }
    return data;
  }, [params]);

  const finalMetrics = useMemo(() => {
    const lastPoint = projections[projections.length - 1];
    const annualRevenue = lastPoint.revenue * 12;
    const exitValue = annualRevenue * params.exitMultiple;
    const totalReturn = lastPoint.cumulativeProfit + exitValue;
    const roi = (totalReturn / params.initialInvestment) * 100;

    return {
      exitValue,
      totalReturn,
      roi: roi.toFixed(1),
      isProfitable: totalReturn > 0
    };
  }, [projections, params]);

  const handleRunAnalysis = async () => {
    setIsAnalyzing(true);
    try {
      const result = await analyzeDeal(params);
      setAiAnalysis(result);
    } catch (error) {
      console.error("Analysis failed:", error);
    } finally {
      setIsAnalyzing(false);
    }
  };

  return (
    <div className="min-h-screen pb-20">
      {/* Navbar */}
      <nav className="sticky top-0 z-50 glass-card px-6 py-4 flex items-center justify-between">
        <div className="flex items-center gap-2">
          <div className="bg-blue-600 p-2 rounded-lg">
            <Calculator className="w-6 h-6 text-white" />
          </div>
          <h1 className="text-xl font-bold tracking-tight">Deal<span className="text-blue-400">Architect</span> <span className="text-xs uppercase bg-blue-900/50 px-2 py-1 rounded text-blue-300 ml-1">v2</span></h1>
        </div>
        <div className="flex gap-4">
          <button 
            onClick={handleRunAnalysis}
            disabled={isAnalyzing}
            className="bg-blue-600 hover:bg-blue-500 disabled:bg-blue-800 text-white px-4 py-2 rounded-lg font-medium transition-all flex items-center gap-2"
          >
            {isAnalyzing ? <RefreshCw className="w-4 h-4 animate-spin" /> : <Zap className="w-4 h-4" />}
            AI Risk Assessment
          </button>
        </div>
      </nav>

      <main className="max-w-7xl mx-auto p-6 grid grid-cols-1 lg:grid-cols-12 gap-6 mt-4">

        {/* Input Panel */}
        <section className="lg:col-span-4 space-y-6">
          <div className="glass-card p-6 rounded-2xl shadow-xl">
            <h2 className="text-lg font-semibold mb-6 flex items-center gap-2">
              <Calculator className="w-5 h-5 text-blue-400" />
              Deal Parameters
            </h2>
            <div className="space-y-4">
              <div>
                <label className="text-xs text-slate-400 uppercase font-semibold">Deal Identity</label>
                <input 
                  type="text" 
                  value={params.dealName}
                  onChange={(e) => setParams({...params, dealName: e.target.value})}
                  className="w-full bg-slate-800/50 border border-slate-700 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 mt-1"
                />
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-xs text-slate-400 uppercase font-semibold">Investment</label>
                  <input 
                    type="number" 
                    value={params.initialInvestment}
                    onChange={(e) => setParams({...params, initialInvestment: Number(e.target.value)})}
                    className="w-full bg-slate-800/50 border border-slate-700 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 mt-1"
                  />
                </div>
                <div>
                  <label className="text-xs text-slate-400 uppercase font-semibold">Monthly Rev</label>
                  <input 
                    type="number" 
                    value={params.monthlyRevenue}
                    onChange={(e) => setParams({...params, monthlyRevenue: Number(e.target.value)})}
                    className="w-full bg-slate-800/50 border border-slate-700 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 mt-1"
                  />
                </div>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-xs text-slate-400 uppercase font-semibold">Mo. Expenses</label>
                  <input 
                    type="number" 
                    value={params.monthlyExpenses}
                    onChange={(e) => setParams({...params, monthlyExpenses: Number(e.target.value)})}
                    className="w-full bg-slate-800/50 border border-slate-700 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 mt-1"
                  />
                </div>
                <div>
                  <label className="text-xs text-slate-400 uppercase font-semibold">Growth (%)</label>
                  <input 
                    type="number" 
                    step="0.1"
                    value={params.growthRate}
                    onChange={(e) => setParams({...params, growthRate: Number(e.target.value)})}
                    className="w-full bg-slate-800/50 border border-slate-700 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 mt-1"
                  />
                </div>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-xs text-slate-400 uppercase font-semibold">Timeline (Mo)</label>
                  <input 
                    type="number" 
                    value={params.timelineMonths}
                    onChange={(e) => setParams({...params, timelineMonths: Number(e.target.value)})}
                    className="w-full bg-slate-800/50 border border-slate-700 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 mt-1"
                  />
                </div>
                <div>
                  <label className="text-xs text-slate-400 uppercase font-semibold">Exit Multiple</label>
                  <input 
                    type="number" 
                    step="0.5"
                    value={params.exitMultiple}
                    onChange={(e) => setParams({...params, exitMultiple: Number(e.target.value)})}
                    className="w-full bg-slate-800/50 border border-slate-700 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 mt-1"
                  />
                </div>
              </div>
            </div>
          </div>

          <div className="glass-card p-6 rounded-2xl shadow-xl bg-gradient-to-br from-blue-900/20 to-purple-900/20">
            <h2 className="text-sm font-semibold mb-4 text-slate-300 uppercase">Projected Outcomes</h2>
            <div className="space-y-4">
              <OutcomeItem 
                label="Potential Exit Value" 
                value={`$${finalMetrics.exitValue.toLocaleString()}`} 
                icon={<ArrowUpRight className="w-4 h-4 text-green-400" />} 
              />
              <OutcomeItem 
                label="Total Deal Return" 
                value={`$${finalMetrics.totalReturn.toLocaleString()}`} 
                icon={<Wallet className="w-4 h-4 text-blue-400" />} 
              />
              <OutcomeItem 
                label="Estimated ROI" 
                value={`${finalMetrics.roi}%`} 
                icon={<TrendingUp className="w-4 h-4 text-purple-400" />} 
              />
            </div>
          </div>
        </section>

        {/* Dashboard Content */}
        <section className="lg:col-span-8 space-y-6">
          <div className="glass-card p-6 rounded-2xl shadow-xl min-h-[400px]">
            <div className="flex items-center justify-between mb-8">
              <h2 className="text-lg font-semibold flex items-center gap-2">
                <BarChart3 className="w-5 h-5 text-blue-400" />
                Financial Trajectory
              </h2>
              <div className="flex gap-4 text-xs">
                <div className="flex items-center gap-1"><div className="w-3 h-3 rounded bg-blue-500"></div> Revenue</div>
                <div className="flex items-center gap-1"><div className="w-3 h-3 rounded bg-purple-500"></div> Cash Flow</div>
              </div>
            </div>
            <div className="h-[350px] w-full">
              <ResponsiveContainer width="100%" height="100%">
                <AreaChart data={projections} margin={{ top: 10, right: 10, left: 0, bottom: 0 }}>
                  <defs>
                    <linearGradient id="colorRev" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.3}/>
                      <stop offset="95%" stopColor="#3b82f6" stopOpacity={0}/>
                    </linearGradient>
                    <linearGradient id="colorProfit" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="5%" stopColor="#a855f7" stopOpacity={0.3}/>
                      <stop offset="95%" stopColor="#a855f7" stopOpacity={0}/>
                    </linearGradient>
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" stroke="#334155" vertical={false} />
                  <XAxis dataKey="month" stroke="#64748b" fontSize={12} tickLine={false} axisLine={false} />
                  <YAxis stroke="#64748b" fontSize={12} tickLine={false} axisLine={false} tickFormatter={(val) => `$${val/1000}k`} />
                  <Tooltip 
                    contentStyle={{ backgroundColor: '#1e293b', border: '1px solid #334155', borderRadius: '8px' }}
                    itemStyle={{ fontSize: '12px' }}
                  />
                  <Area type="monotone" dataKey="revenue" stroke="#3b82f6" fillOpacity={1} fill="url(#colorRev)" />
                  <Area type="monotone" dataKey="cumulativeProfit" stroke="#a855f7" fillOpacity={1} fill="url(#colorProfit)" />
                </AreaChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* AI Insights Section */}
          <div className="glass-card p-6 rounded-2xl shadow-xl">
             <div className="flex items-center justify-between mb-6">
                <h2 className="text-lg font-semibold flex items-center gap-2">
                  <Zap className="w-5 h-5 text-yellow-400" />
                  AI Architect Insights
                </h2>
                {aiAnalysis && (
                  <div className="flex items-center gap-2 px-3 py-1 bg-slate-800 rounded-full">
                    <span className="text-xs font-bold text-slate-400 uppercase">Risk Level:</span>
                    <span className={`text-sm font-bold ${aiAnalysis.riskScore > 70 ? 'text-red-400' : aiAnalysis.riskScore > 40 ? 'text-yellow-400' : 'text-green-400'}`}>
                      {aiAnalysis.riskScore}%
                    </span>
                  </div>
                )}
             </div>

             {!aiAnalysis && !isAnalyzing ? (
               <div className="flex flex-col items-center justify-center py-12 text-center">
                  <div className="bg-slate-800/50 p-4 rounded-full mb-4">
                    <Target className="w-10 h-10 text-slate-500" />
                  </div>
                  <h3 className="text-lg font-medium text-slate-300">Run AI Analysis</h3>
                  <p className="text-slate-500 text-sm max-w-sm mt-2">
                    Click the "AI Risk Assessment" button to generate a detailed strategic evaluation of this deal.
                  </p>
               </div>
             ) : isAnalyzing ? (
               <div className="py-20 flex flex-col items-center gap-4">
                 <div className="relative">
                    <div className="w-16 h-16 border-4 border-blue-600/20 border-t-blue-500 rounded-full animate-spin"></div>
                    <Zap className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-6 h-6 text-yellow-400 animate-pulse" />
                 </div>
                 <p className="text-blue-400 animate-pulse font-medium">Architect is modeling scenarios...</p>
               </div>
             ) : aiAnalysis && (
               <div className="space-y-8 animate-in fade-in duration-500">
                  <div className="bg-blue-900/20 border border-blue-800/50 p-4 rounded-xl">
                    <p className="text-blue-100 text-sm leading-relaxed">{aiAnalysis.summary}</p>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="space-y-3">
                      <h4 className="text-xs font-bold text-green-400 uppercase tracking-wider flex items-center gap-1">
                        <TrendingUp className="w-3 h-3" /> Strengths
                      </h4>
                      <ul className="space-y-2">
                        {aiAnalysis.strengths.map((s, idx) => (
                          <li key={idx} className="text-xs flex items-start gap-2 text-slate-300">
                            <ChevronRight className="w-3 h-3 mt-0.5 text-green-500 flex-shrink-0" /> {s}
                          </li>
                        ))}
                      </ul>
                    </div>
                    <div className="space-y-3">
                      <h4 className="text-xs font-bold text-red-400 uppercase tracking-wider flex items-center gap-1">
                        <ShieldAlert className="w-3 h-3" /> Weaknesses
                      </h4>
                      <ul className="space-y-2">
                        {aiAnalysis.weaknesses.map((w, idx) => (
                          <li key={idx} className="text-xs flex items-start gap-2 text-slate-300">
                            <ChevronRight className="w-3 h-3 mt-0.5 text-red-500 flex-shrink-0" /> {w}
                          </li>
                        ))}
                      </ul>
                    </div>
                  </div>

                  <div className="space-y-3">
                    <h4 className="text-xs font-bold text-blue-400 uppercase tracking-wider">Strategic Recommendation</h4>
                    <p className="text-sm text-slate-300 leading-relaxed italic bg-slate-800/40 p-4 rounded-lg border-l-4 border-blue-500">
                      "{aiAnalysis.strategicAdvice}"
                    </p>
                  </div>
               </div>
             )}
          </div>
        </section>
      </main>

      {/* Persistent Call-to-Action */}
      <div className="fixed bottom-6 left-1/2 -translate-x-1/2 glass-card px-8 py-4 rounded-full shadow-2xl flex items-center gap-8 border-blue-500/30">
        <div className="text-center">
          <p className="text-[10px] text-slate-400 uppercase font-bold">Total Capital</p>
          <p className="text-lg font-bold text-blue-400">${params.initialInvestment.toLocaleString()}</p>
        </div>
        <div className="w-px h-8 bg-slate-700"></div>
        <div className="text-center">
          <p className="text-[10px] text-slate-400 uppercase font-bold">ROI</p>
          <p className={`text-lg font-bold ${Number(finalMetrics.roi) > 0 ? 'text-green-400' : 'text-red-400'}`}>{finalMetrics.roi}%</p>
        </div>
        <button 
          onClick={handleRunAnalysis}
          className="bg-blue-600 hover:bg-blue-500 text-white px-6 py-2 rounded-full font-bold shadow-lg shadow-blue-900/20 transition-all flex items-center gap-2"
        >
          {isAnalyzing ? "Processing..." : "Negotiate"}
          <ChevronRight className="w-4 h-4" />
        </button>
      </div>
    </div>
  );
};

const OutcomeItem: React.FC<{ label: string, value: string, icon: React.ReactNode }> = ({ label, value, icon }) => (
  <div className="flex items-center justify-between p-3 bg-slate-800/30 rounded-xl border border-slate-700/50">
    <div className="flex items-center gap-3">
      <div className="p-2 bg-slate-700/50 rounded-lg">
        {icon}
      </div>
      <span className="text-xs font-medium text-slate-400">{label}</span>
    </div>
    <span className="font-bold text-slate-100">{value}</span>
  </div>
);

export default App;

