html { overflow-y: scroll; }
body {
    font-family: 'Outfit', 'Inter', 'Pretendard', -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
    -webkit-font-smoothing: antialiased;
}
::-webkit-scrollbar { width: 6px; height: 6px; }
::-webkit-scrollbar-track { background: transparent; }
::-webkit-scrollbar-thumb {
    background: #CBD5E1;
    border-radius: 99px;
    border: 2px solid transparent;
    background-clip: content-box;
}
::-webkit-scrollbar-thumb:hover { background-color: #94A3B8; }
.spinner {
    width: 32px; height: 32px;
    border: 3px solid rgba(79, 70, 229, 0.1);
    border-radius: 50%;
    border-top-color: #4F46E5;
    animation: spin 0.8s cubic-bezier(0.4, 0, 0.2, 1) infinite;
}
@keyframes spin { to { transform: rotate(360deg); } }
.tooltip-trigger { position: relative; cursor: help; }
.tooltip-content {
    visibility: hidden; position: absolute; z-index: 100;
    bottom: 125%; left: 50%; transform: translateX(-50%) translateY(5px);
    background-color: #0F172A; color: white; padding: 10px 14px;
    border-radius: 10px; font-size: 11px; width: 220px; opacity: 0;
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1); pointer-events: none;
    line-height: 1.5; border: 1px solid rgba(255, 255, 255, 0.1);
}
.tooltip-trigger:hover .tooltip-content {
    visibility: visible; opacity: 1; transform: translateX(-50%) translateY(0);
}
.card-enterprise {
    background-color: white; border: 1px solid #E2E8F0;
    border-radius: 12px; box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.05);
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}
.input-enterprise {
    background-color: #FFFFFF; border: 1px solid #CBD5E1;
    border-radius: 8px; padding: 10px 14px; font-size: 0.925rem;
    color: #0F172A; width: 100%;
}
.quadrant-grid {
    display: grid; grid-template-columns: 1fr 1fr; grid-template-rows: 1fr 1fr;
    border: 1.5px solid #E2E8F0; position: relative; background-color: #F8FAFC;
    border-radius: 8px; overflow: hidden;
}
.quadrant-dot {
    position: absolute; width: 14px; height: 14px; background-color: #4F46E5;
